//! Variables (JQuery Type)
const sidebarOpenerButton = $(".header__content-justify"),
  sidebarCloserButton = $(".sidebar__close"),
  openerSidebar = $(".sidebarRes"),
  darkBackground = $(".bg"),
  recentSectionChangerButton = $(".recent__side-opener"),
  recentContentRight = $(".recent__content-right"),
  recentContentLeft = $(".recent__content-left"),
  openSidebar = () => {
    $("html").css("overflow", "hidden"),
      openerSidebar.css({ left: "0", overflow: "auto", height: "100vh" }),
      darkBackground.css("display", "block"),
      sidebarOpenerButton.css("display", "none");
  },
  closeSidebar = () => {
    $("html").css("overflow", "auto"),
      openerSidebar.css("left", "-50rem"),
      sidebarOpenerButton.css("display", "block"),
      darkBackground.css("display", "none");
  },
  changeRecentSection = () => {
    "hidden" === recentContentRight.css("visibility")
      ? (recentContentRight.css("visibility", "visible"),
        recentContentRight.animate({ opacity: "1" }, 200),
        recentContentLeft.css({ visibility: "hidden", opacity: "0" }),
        recentSectionChangerButton.css("background", "transparent"))
      : (recentContentRight.css({ visibility: "hidden", opacity: "0" }),
        recentContentLeft.css("visibility", "visible"),
        recentContentLeft.animate({ opacity: "1" }, 200),
        recentSectionChangerButton.css("background", "white"));
  }; //! Calling Functions
sidebarOpenerButton.on("click", openSidebar),
  sidebarCloserButton.on("click", closeSidebar),
  recentSectionChangerButton.on("click", changeRecentSection);
